CREATE DATABASE CakeData;
GO

USE CakeData;
GO

CREATE TABLE Cakes (
    id INT PRIMARY KEY,
    name NVARCHAR(255),
    description NVARCHAR(255),
);
GO

CREATE TABLE CakeImages (
    id_cake INT,
    url NVARCHAR(255),
    PRIMARY KEY (id_cake, url),
    FOREIGN KEY (id_cake) REFERENCES Cakes(id) on delete cascade
);
GO

CREATE TABLE CakeSizes (
    id_cake INT,
    size NVARCHAR(255),
    price INT,
    old_price INT,
    PRIMARY KEY (id_cake, size),
    FOREIGN KEY (id_cake) REFERENCES Cakes(id) on delete cascade
);
GO

CREATE TABLE Clients (
   id INT PRIMARY KEY IDENTITY(1,1),
   name NVARCHAR(255) NOT NULL,
   email NVARCHAR(255) NOT NULL UNIQUE,
   phone NVARCHAR(255) UNIQUE,
   password NVARCHAR(255) NOT NULL,
);
GO

CREATE TABLE Orders (
   id INT PRIMARY KEY IDENTITY(1,1),
   id_client INT,
   id_cake INT,
   size NVARCHAR(255),
   quantity INT,
   notice NVARCHAR(max),
   date NVARCHAR(255),
   address NVARCHAR(255),
   total INT,
   FOREIGN KEY (id_client) REFERENCES Clients(id) on delete cascade,
   FOREIGN KEY (id_cake) REFERENCES Cakes(id) on delete cascade
);
GO

